function setCookie(e,o,n){var t;if(n){var i=new Date;i.setTime(i.getTime()+24*n*60*60*1e3),t="; expires="+i.toGMTString()}else t="";document.cookie=encodeURIComponent(e)+"="+encodeURIComponent(o)+t+"; path=/"}function getCookie(e){for(var o=encodeURIComponent(e)+"=",n=document.cookie.split(";"),t=0;t<n.length;t++){for(var i=n[t];" "===i.charAt(0);)i=i.substring(1,i.length);if(0===i.indexOf(o))return decodeURIComponent(i.substring(o.length,i.length))}return null}function deleteCookie(e){setCookie(e,"",-1)}