function setCookie(e,o,t){let n;if(t){let e=new Date;e.setTime(e.getTime()+24*t*60*60*1e3),n="; expires="+e.toGMTString()}else n="";document.cookie=encodeURIComponent(e)+"="+encodeURIComponent(o)+n+"; path=/"}function getCookie(e){const o=encodeURIComponent(e)+"=",t=document.cookie.split(";");for(const e of t){let t=e;for(;" "===t.charAt(0);)t=t.substring(1,t.length);if(0===t.indexOf(o))return decodeURIComponent(t.substring(o.length,t.length))}return null}function deleteCookie(e){setCookie(e,"",-1)}