$(document).ready((function(){if($(".sidebar .far.fa-heart").length){const a=$(this),e=$("#text-container").attr("data-textid");$.ajax({type:"POST",url:"ajax/togglelike.php",data:{id:e,toggle_like:!1}}).done((function(e){!0===e.user_liked&&t(a)})).fail((function(t,a,e){}))}function t(t){t.toggleClass("fas far");const a=parseInt(t.siblings("small").text());t.hasClass("fas")?t.siblings("small").text(a+1):t.siblings("small").text(a-1)}$("span.fa-heart").on("click",(function(){const a=$(this),e=a.attr("data-idText");t(a),$.ajax({type:"POST",url:"ajax/togglelike.php",data:{id:e,toggle_like:!0}}).done((function(e){e.error_msg&&t(a)})).fail((function(e,i,n){t(a)}))}))}));