$(document).ready((function(){$("i.fa-heart").on("click",(function(){var a=$(this),e=a.attr("data-idText");$.ajax({type:"POST",url:"ajax/togglelike.php",data:{id:e}}).done((function(e){if(e.error_msg)alert("Oops! There was an unexpected error");else{a.toggleClass("fas far");var t=parseInt(a.siblings("small").text());a.hasClass("fas")?a.siblings("small").text(t+1):a.siblings("small").text(t-1)}})).fail((function(a,e,t){alert("Oops! There was an unexpected error")}))}))}));