$(document).ready((function(){$("i.fa-heart").on("click",(function(){var a=$(this),t=a.attr("data-idText");$.ajax({type:"POST",url:"ajax/togglelike.php",data:{id:t}}).done((function(t){if(t.error_msg)alert("error!");else{a.toggleClass("fas far");var l=parseInt(a.siblings("small").text());a.hasClass("fas")?a.siblings("small").text(l+1):a.siblings("small").text(l-1)}})).fail((function(a,t,l){alert(l)}))}))}));