$(document).ready((function(){$("i.fa-heart").on("click",(function(){const e=$(this),a=e.attr("data-idText");$.ajax({type:"POST",url:"ajax/togglelike.php",data:{id:a}}).done((function(a){if(a.error_msg)alert("Oops! There was an unexpected error");else{e.toggleClass("fas far");const a=parseInt(e.siblings("small").text());e.hasClass("fas")?e.siblings("small").text(a+1):e.siblings("small").text(a-1)}})).fail((function(e,a,t){alert("Oops! There was an unexpected error")}))}))}));