$(document).ready((function(){var a=[],e=[],o=[],t=[];$.ajax({type:"GET",url:"ajax/getstats.php",data:{days:7},async:!1,dataType:"json"}).done((function(r){a=r.created,e=r.modified,o=r.learned,t=r.forgotten})).fail((function(){}));var r=document.getElementById("myChart").getContext("2d");new Chart(r,{type:"bar",data:{labels:["6 days ago","5 days ago","4 days ago","3 days ago","2 days ago","Yesterday","Today"],datasets:[{label:"New",data:a,backgroundColor:"#1e90ff",borderColor:"#1e90ff",fill:!1},{label:"Reviewed",data:e,backgroundColor:"#ffa500",borderColor:"#ffa500",fill:!1},{label:"Learned",data:o,backgroundColor:"#3cb371",borderColor:"#3cb371",fill:!1},{label:"Forgotten",data:t,backgroundColor:"#ff6347",borderColor:"#ff6347",fill:!1}]},options:{scales:{yAxes:[{ticks:{beginAtZero:!0,suggestedMax:10,userCallback:function(a,e,o){if(Math.floor(a)===a)return a}},scaleLabel:{display:!0,labelString:"Number of words"}}]},maintainAspectRatio:!1}})}));