function googleLogIn(e){const o=decodeJwtResponse(e.credential);$.ajax({type:"POST",data:{id:o.sub,name:o.name,email:o.email,"time-zone":Intl.DateTimeFormat().resolvedOptions().timeZone},url:"ajax/google_oauth.php"}).done((function(e){null==e.error_msg?window.location.replace("/texts"):showMessage(e.error_msg,"alert-danger")})).fail((function(e,o,t){showMessage("Oops! There was an unexpected error when trying to register you. Please try again later.","alert-danger")}))}function decodeJwtResponse(e){let o=e.split(".")[1].replace(/-/g,"+").replace(/_/g,"/"),t=decodeURIComponent(atob(o).split("").map((function(e){return"%"+("00"+e.charCodeAt(0).toString(16)).slice(-2)})).join(""));return JSON.parse(t)}