$(document).ready((function(){$("#form_login").on("submit",(function(e){e.preventDefault();const n=$("#form_login").serialize();$.ajax({type:"POST",url:"ajax/login.php",data:n}).done((function(e){null==e.error_msg?window.location.replace("/texts"):showMessage(e.error_msg,"alert-danger")})).fail((function(e,n,o){showMessage("Oops! There was an unexpected error when trying to log you in. Please try again later.","alert-danger")}))}))}));