$(document).ready((function(){$("#form_login").on("submit",(async function(o){o.preventDefault();const e=$("#form_login").serialize();try{const o=await fetch("/ajax/login.php",{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:e});if(!o.ok)throw new Error(`HTTP error: ${o.status}`);const r=await o.json();if(!r.success)throw new Error(r.error_msg||"Login failed");window.location.replace("/texts")}catch(o){showMessage(o.message,"alert-danger")}}))}));