async function handleLogout(o=!1){"undefined"!=typeof google&&google.accounts.id.disableAutoSelect();try{const e=new URLSearchParams;e.append("after_user_delete",o);const t=await fetch("/ajax/logout.php",{method:"POST",body:e});if(!t.ok)throw new Error(`HTTP error: ${t.status}`);const a=await t.json();if(!a.success)throw new Error(a.error_msg||"Failed to logout.");window.location.href="/"}catch(o){alert(`Oops! ${o.message}`)}}