$(document).ready((function(){$.ajax({type:"GET",url:"ajax/getstats.php",data:{type:"words"},dataType:"json"}).done((function(t){const e=parseInt(t[0].count),a=parseInt(t[1].count),n=parseInt(t[2].count),o=parseInt(t[3].count),r=e+a+n+o,c=document.getElementById("total-stats-canvas").getContext("2d");new Chart(c,{type:"bar",data:{labels:["Words"],datasets:[{label:"Learned",data:[e],backgroundColor:"#3cb371"},{label:"Learning",data:[a],backgroundColor:"#ffa500"},{label:"New",data:[n],backgroundColor:"#1e90ff"},{label:"Forgotten",data:[o],backgroundColor:"#ff6347"}]},options:{indexAxis:"y",responsive:!0,scales:{x:{stacked:!0},y:{stacked:!0,display:!1}}}}),$("#learned-count").text(e),$("#learned-percentage").text((e/r*100).toFixed(2).toLocaleString("en-US")),$("#learning-count").text(a),$("#learning-percentage").text((a/r*100).toFixed(2).toLocaleString("en-US")),$("#new-count").text(n),$("#new-percentage").text((n/r*100).toFixed(2).toLocaleString("en-US")),$("#forgotten-count").text(o),$("#forgotten-percentage").text((o/r*100).toFixed(2).toLocaleString("en-US")),$("#total-count").text(e+a+n+o)})).fail((function(){$("#total-stats-canvas").replaceWith("<div class='d-flex align-items-center justify-content-center' style='min-height:400px;'><p class='text-danger'>Error: no data to display</p></div>")}))}));